mapper.update:
update student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, degree = #{degree}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, graduation_date = #{graduationDate}, clazz_id = #{clazzId}, violation_count = #{violationCount}, violation_score = #{violationScore}, update_time = #{updateTime}
where id = #{id}

mapper.delete:
delete from student
where id in

    #{id}

mapper.select:
select s.*, c.name as clazzName from student s left join clazz c on s.clazz_id = c.id


        s.name like concat('%', #{name}, '%')


        and s.degree = #{degree}


        and s.clazz_id = #{clazzId}


order by s.update_time desc
